        debugger
        const caret = event.target.firstChild;

        if (panel.style.maxHeight) {
            panel.style.maxHeight = null;
            caret.classList.remove('flipped');
        } else {
            panel.style.maxHeight = panel.scrollHeight + 'px';
            caret.classList.add('flipped');
        }
    }

